import React, { useEffect, useState } from "react";
import { Space, Button, Text, Popover, Modal, TextInput } from "@mantine/core";
import {
    Elements,
    CardElement,
    useElements,
    useStripe,
} from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import "../../css/payment.css";
import Logo from "./logo";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { useForm } from "@mantine/form";
import { PaymentItemsContext } from "../general/paymentItemsContext";
import axios from "axios";

const stripePromise = loadStripe(
    "pk_test_51L5VsBHihNXOYuTY3oM7Tf3knsg5gaElFMpmJ83reKHkYVU8EVZF7Na9VlfuL45nm7aJX1qEVjfHgc6zX1SftPe700qT0FsdZd"
);

const handleSubmit =
    (
        stripe,
        elements,
        setFailed,
        setMessage,
        setSuccess,
        paymentItems,
        setPaymentItems
    ) =>
        async () => {
            const cardElement = elements.getElement(CardElement);

            const { error, paymentMethod } = await stripe.createPaymentMethod({
                type: "card",
                card: cardElement,
            });

            if (error) {
                console.log("[error]", error);
                setMessage(error.message);
                setFailed(true);
            } else {
                console.log("[PaymentMethod]", paymentMethod);
                setSuccess(true);
                if (localStorage.getItem("cart")) {
                    let cart = JSON.parse(localStorage.getItem("cart"));
                    paymentItems.map((item) => {
                        for (let i = 0; i < cart.length; i++) {
                            if (cart[i].id === item.id) {
                                cart.splice(i, 1);
                            }
                        }
                    });
                    setMessage("Thanh toán thành công");
                    localStorage.setItem("cart", JSON.stringify(cart));
                } else {
                    setMessage("Thanh toán thành công");
                    // ... SEND to your API server to process payment intent
                    const products = paymentItems.map((paymentItem) => {
                        return {
                            id: paymentItem.id,
                            quantity: paymentItem.count,
                        };
                    });

                    const billInfo = {
                        product: products,
                        userId: sessionStorage.getItem("id"),
                    };

                    await axios
                        .post(
                            `http://localhost:8080/api/payment/make`,
                            billInfo
                        )
                        .then((response) => {
                            console.log(response);
                        })
                        .catch((error) => {
                            console.log(error);
                        });

                    setPaymentItems([]);
                }
            }
        };

const PaymentForm = ({ total }) => {
    const stripe = useStripe();
    const elements = useElements();
    const navigate = useNavigate();
    const [failed, setFailed] = useState(false);
    const [success, setSuccess] = useState(false);
    const [message, setMessage] = useState("");
    const [paymentItems, setPaymentItems] =
        React.useContext(PaymentItemsContext);
    const form = useForm({
        initialValues: {
            fullName: "",
            address: "",
            phonenum: "",
        },

        validate: {
            fullName: (value) =>
                /[a-z0-9A-Z_ÀÁÂÃÈÉÊÌÍÒÓÔÕÙÚĂĐĨŨƠàáâãèéêìíòóôõùúăđĩũơƯĂẠẢẤẦẨẪẬẮẰẲẴẶẸẺẼỀỀỂưăạảấầẩẫậắằẳẵặẹẻẽềềểỄỆỈỊỌỎỐỒỔỖỘỚỜỞỠỢỤỦỨỪễếệỉịọỏốồổỗộớờởỡợụủứừỬỮỰỲỴÝỶỸửữựỳỵỷỹ]$/.test(
                    value
                )
                    ? null
                    : "Vui lòng nhập tên hợp lệ",
            phonenum: (value) =>
                /^(0?)(3[2-9]|5[6|8|9]|7[0|6-9]|8[0-6|8|9]|9[0-4|6-9])[0-9]{7}$/.test(
                    value
                )
                    ? null
                    : " Số điện thoại không hợp lệ",
        },
    });

    const user = sessionStorage.getItem("userName");

    return (
        <form
            className="payment-form-wrapper"
            onSubmit={form.onSubmit((values) => console.log(values))}
        >
            <>
                <Logo classname="form-company-logo" />
                <Space h="md" />
                <Text
                    weight={600}
                    style={{ fontSize: 36, textAlign: "center" }}
                >
                    Thanh toán
                </Text>
                <Space h="md" />
                <Text style={{ textAlign: "left" }}>
                    Vui lòng điền đầy đủ thông tin
                </Text>
                <Space h="md" />
                <Text style={{ textAlign: "left" }}>
                    * Chúng tôi chấp nhận các phương thức thanh toán khác nhau
                    như visa, mastercard, ...
                </Text>
                <Space h="md" />
                {!user ? (
                    <>
                        <TextInput
                            label="Tên"
                            placeholder="Tên của bạn"
                            className="form-username-input"
                            {...form.getInputProps("fullName")}
                        />
                        <Space h="md" />
                        <TextInput
                            label="Địa chỉ"
                            placeholder="Địa chỉ"
                            className="form-password-input"
                            {...form.getInputProps("address")}
                        />
                        <Space h="md" />
                        <TextInput
                            label="Số điện thoại"
                            placeholder="số điện thoại"
                            className="form-username-input"
                            {...form.getInputProps("phonenum")}
                        />{" "}
                    </>
                ) : null}
                <Text style={{ fontSize: 30, fontWeight: "500" }}>
                    Tổng tiền:{" "}
                    {new Intl.NumberFormat("vi-VN", {
                        style: "currency",
                        currency: "VND",
                    }).format(total)}
                </Text>
                <Popover
                    opened={failed}
                    onClose={() => setFailed(false)}
                    target={<div></div>}
                    width={260}
                    position="top"
                    withArrow
                >
                    <Text size="sm">{message}</Text>
                </Popover>
                <Modal
                    opened={success}
                    onClose={() => {
                        navigate("/");
                        setSuccess(false);
                    }}
                    size={300}
                    withCloseButton={false}
                    centered
                >
                    <Text size="xl" style={{ textAlign: "center" }}>
                        {message}
                    </Text>
                    <Space h="md" />
                    <Link to="/">
                        <Button color="dark" className="form-signin-submit-btn">
                            Trở về trang chủ
                        </Button>
                    </Link>
                </Modal>
                <CardElement />
                <Space h="md" />
                <Button
                    onClick={handleSubmit(
                        stripe,
                        elements,
                        setFailed,
                        setMessage,
                        setSuccess,
                        paymentItems,
                        setPaymentItems
                    )}
                    color="dark"
                    className="form-signin-submit-btn"
                    style={{ marginBottom: 10 }}
                >
                    MUA
                </Button>
                <Link to="/cart">
                    <Button color="dark" className="form-signin-submit-btn">
                        Quay lại giỏ hàng
                    </Button>
                </Link>
            </>
        </form>
    );
};

export default function CheckoutForm({ total }) {
    return (
        <Elements stripe={stripePromise}>
            <PaymentForm total={total} />
        </Elements>
    );
}
