function joiResolver(schema) {
  var _schema = schema;
  return function (values) {
    var parsed = _schema.validate(values, {
      abortEarly: false
    });
    if (!parsed.error) {
      return {};
    }
    var results = {};
    parsed.error.details.forEach(function (error) {
      results[error.path.join(".")] = error.message;
    });
    return results;
  };
}
export { joiResolver };
