import Chart from 'react-apexcharts'
import { Card, CardHeader, CardTitle, CardBody, CardSubtitle } from 'reactstrap'

const ApexRadiarChart = () => {
    const data = {
        labels: ['Sản phẩm A', 'Sản phẩm B', 'Sản phẩm C', 'Sản phẩm D', 'Sản phẩm E'],
        datasets: [
          {
            label: 'Doanh thu',
            data: [500, 1000, 750, 900, 1200],
            backgroundColor: 'rgba(75, 192, 192, 0.6)', // Màu nền của cột doanh thu
            borderColor: 'rgba(75, 192, 192, 1)', // Màu viền của cột doanh thu
            borderWidth: 1, // Độ dày viền
          },
          {
            label: 'Số lượng',
            data: [50, 80, 65, 70, 90],
            backgroundColor: 'rgba(54, 162, 235, 0.6)', // Màu nền của cột số lượng
            borderColor: 'rgba(54, 162, 235, 1)', // Màu viền của cột số lượng
            borderWidth: 1, // Độ dày viền
          },
        ],
      };
    
      // Cấu hình biểu đồ
      const options = {
        scales: {
          y: {
            beginAtZero: true,
            min: 0,
            max: 1500,
          },
        },
      };
    
      return (
        <div className="chart-container">
          <h2>Biểu đồ thống kê doanh thu và số lượng bán chạy nhất</h2>
          <div className="chart">
            <Bar data={data} options={options} />
          </div>
        </div>
      );
}

export default ApexRadiarChart
